package Baekjoon._220103;

import java.util.Scanner;

public class Main_10951_aplusb {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a, b;
		Scanner sc = new Scanner(System.in);
		
		while(sc.hasNextInt())
		{
			a = sc.nextInt();
			b = sc.nextInt();
			System.out.println(a+b);
		}
		
	}

}
